import React from "react";
import "../css/logincontents.css";
import {Link} from "react-router-dom";

function LoginContents(){

  function submitInfo(){
    const USER_INFO = "userInfo";
    
    const loadInfo = localStorage.getItem(USER_INFO);
    console.log(localStorage.getItem(USER_INFO));
  }

  function checkInfo(){
    const loginId = document.querySelector(".login-id");
    const loginValue = loginId.value;
    
    if(loginId === "") {
      alert("아이디를 입력해주세요.");
    } else {
      alert("perfect")
    }
  }
 
  return(
    <>
      <div className="wrap lg_contents">
        <div className="container">
          <h3 className="title">Login</h3>
          <div className="lg-box">
            <input type="text" placeholder="ID" className="login-id"/>
            <input type="password" placeholder="PASSWORD"/>
          </div>

          <div className="bt-box">
            <button type="button" onClick={checkInfo} className="bt">
              Sign In
            </button>
            <Link to="/join" className="bt">
              Sign Up
            </Link>
          </div>
        </div>
      </div>
    </>
  )
}

export default LoginContents;
