import React from "react";
import "../css/joincontents.css";

function JoinContents(){

  
  const USER_LS = "userInfo";

  function handleSubmit(event){
    event.preventDefault();
  }
  
  // function submitId(text){
  //   localStorage.setItem(USER_LS, text);
  // }

  // function askForId() {
  //   alert("아이디를 입력해주세요");
  //   form.classList.add(SHOWING_ON);
  //   form.addEventListener("submit", handleSubmit);
  // }

  function submitId(){
    const USER_ID = document.querySelector(".user-id");
    const IdValue = USER_ID.value;
    console.log(IdValue);

    if(IdValue === null){
      alert("아이디를 입력해주세요.");
    //   askForId();
    } else {
      alert("회원가입이 완료되었습니다.");
    }
  }

  return(
    <>
      <div className="wrap jo_contents">
        <div className="form">
          <h3 className="title">Join Us</h3>
          <table border="1">
            <caption>회원 기본정보</caption>
            <tbody>
              <tr>
                <th scope="row">아이디</th>
                <td>
                  <input minLength="4" maxLength="16" className="user-id" type="text" />
                  <div>
                    <p className="error">아이디를 입력해주세요</p>
                    <p className="guide">영문 소문자/숫자, 4-16자</p>
                  </div>
                </td>
              </tr>
              <tr>
                <th scope="row">비밀번호</th>
                <td>
                  <input autoComplete="off" minLength="4" maxLength="16" type="password"/>
                  <div>
                    <p className="guide">영문 소문자/숫자, 4-16자</p>
                  </div>
                </td>
              </tr>
              <tr>
                <th scope="row">비밀번호 확인</th>
                <td>
                  <input autoComplete="off" minLength="4" maxLength="16" type="password"/>
                </td>
              </tr>
              <tr className="phone">
                <th scope="row">핸드폰</th>
                <td>
                  <select name="mobile_num" >
                    <option value="010">010</option>
                    <option value="011">011</option>
                    <option value="016">016</option>
                    <option value="017">017</option>
                    <option value="018">018</option>
                    <option value="019">019</option>
                  </select>
                  -
                  <input maxLength="4" type="text" />
                  -
                  <input maxLength="4" type="text" />
                </td>
              </tr>
            </tbody>
          </table>
          <button type="button" className="bt_join" onClick={submitId}>가입하기</button>
        </div>
      </div>
    </>
  )
}

export default JoinContents;
